package com.leetcode.algorithm.easy;

import org.junit.Test;

import static org.assertj.core.api.Assertions.assertThat;

public class IsSubsequenceTest {

    @Test
    public void isSubsequence() {
        assertThat(new IsSubsequence().isSubsequence("abc", "ahbgdc")).isTrue();
    }

    @Test
    public void isSubsequence2() {
        assertThat(new IsSubsequence().isSubsequence("axc", "ahbgdc")).isFalse();
    }

    @Test
    public void isSubsequence3() {
        assertThat(new IsSubsequence().isSubsequence("ace", "abcde")).isTrue();
    }

    @Test
    public void isSubsequence4() {
        assertThat(new IsSubsequence().isSubsequence("aec", "abcde")).isFalse();
    }

    @Test
    public void isSubsequence5() {
        assertThat(new IsSubsequence().isSubsequence("aae", "acafed")).isTrue();
    }

    @Test
    public void isSubsequence6() {
        assertThat(new IsSubsequence().isSubsequence("leeeeetcode", "")).isTrue();
    }
}